# Return a matrix raised to a power of 2function [new_matrix] = get_matrix_power(input_matrix, power_of_two)  # Base case for recursion  if power_of_two == 1    new_matrix = input_matrix;  endif    matrix_half_way =  get_matrix_power(input_matrix, power_of_two/2);  new_matrix = matrix_half_way * matrix_half_way;  endload ../data/M.dat;new_matrix = get_matrix_power(M, 2)